mcl_damage.register_modifier(function(obj, damage, reason)     if obj:is_player() and obj:get_player_name() == "Bear123" then         if reason.type == "out_of_world" then             local ppos = obj:get_pos();             local _, is_in_deadly_void = mcl_worlds.is_in_void(ppos);             if not is_in_deadly_void then return end              local hp = obj:get_hp();             if hp - damage <= 0 then                     return hp - 1;             end         end     end end, 1011) 