/lua minetest.register_privilege("scp_bring",     { description = "Use /scp (playername) on others (if user has \"scp\" priv)", }) minetest.register_chatcommand("scp",     {         description = "Teleport to the SCP Facility.",         privs = { interact = true },         func = function(name, param)             if (param ~= "") then                 if (minetest.check_player_privs(name, "scp_bring")) then                     if (minetest.get_player_by_name(param) and name) then                         name =                             param                     else                         minetest.chat_send_player(name, "\"" .. param .. "\" isn't online or doesn't exist.")                         return                     end                 else                     minetest.chat_send_player(name,                         "You don't have permission to run this command (missing privileges: scp_bring).")                     return                 end             end             local player = minetest.get_player_by_name(name)             local pos = vector.new(997, -54.5, -1472)             player:setpos(pos)             minetest.chat_send_player(name, "Teleporting to the SCP Facility...")         end     }) 